import { useState, useEffect } from "react";
import { useParams, Link, useNavigate } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeftLong } from "@fortawesome/free-solid-svg-icons";

export default function Country() {
  const { country } = useParams();
  const history = useNavigate();
  const [countryData, setCountryData] = useState({});
  const [borders, setBorders] = useState([]);

  useEffect(() => {
    fetch(`https://restcountries.com/v2/name/${country}`)
      .then((res) => res.json())
      .then((json) => {
        setCountryData(json[0]);
        console.log("country set");
      })
      .catch((error) => console.log(error));
  }, [country]);

  useEffect(() => {
    if (countryData.borders) {
      fetch(
        `https://restcountries.com/v2/alpha?codes=${countryData.borders.join(
          ","
        )}`
      )
        .then((res) => res.json())
        .then((json) => {
          setBorders(json.map((country) => country.name));
        })
        .catch((error) => console.log(error));
    }
  }, [countryData]);

  return (
    <div className='max-w-6xl mx-auto px-6'>
      <button className='px-6 py-1 bg-dark-blue space-x-2 flex justify-center items-center drop-shadow-2xl rounded-md my-12' onClick={() => history(-1)}>
        <FontAwesomeIcon icon={faArrowLeftLong} />
        <span className='text-sm font-light'>Back</span>
      </button>
      <div className='flex'>
        <div className='flex-1'>
          <img
            className='border-8 border-dark-blue rounded-lg'
            src={countryData.flags?.svg}
            alt='flag'
          />
        </div>
        <div className='flex-1 flex flex-col justify-center'>
          <div className='ml-16 my-4 text-3xl font-semibold'>
            {countryData.name}
          </div>
          <div className='ml-16 flex text-sm'>
            <div>
              <div>
                <span className='font-semibold'>Native Name:</span>{" "}
                {countryData.nativeName}
              </div>
              <div>
                <span className='font-semibold'>Population:</span>{" "}
                {countryData.population?.toLocaleString("en-US")}
              </div>
              <div>
                <span className='font-semibold'>Region:</span>{" "}
                {countryData.region}
              </div>
              <div>
                <span className='font-semibold'>Sub Region:</span>{" "}
                {countryData.subregion}
              </div>
              <div>
                <span className='font-semibold'>Capital:</span>{" "}
                {countryData.capital}
              </div>
            </div>
            <div className='ml-8'>
              <div>
                <span className='font-semibold'>Top Level Domain:</span>{" "}
                {countryData.topLevelDomain?.[0]}
              </div>
              <div>
                <span className='font-semibold'>Currencies:</span>{" "}
                {countryData.currencies?.map((curr) => curr.name).join(", ")}
              </div>
              <div>
                <span className='font-semibold'>Languages:</span>{" "}
                {countryData.languages?.map((curr) => curr.name).join(", ")}
              </div>
            </div>
          </div>
          <div className='ml-16 mt-6'>
            Border Countries:{" "}
            <span className='text-sm flex flex-wrap gap-2'>
              {borders.map((border) => (
                <Link key={border} to={`/${border.toLowerCase()}`}>
                  <button className='px-6 py-1 bg-dark-blue drop-shadow-2xl rounded-md'>
                    {border}
                  </button>
                </Link>
              ))}
            </span>
          </div>
        </div>
      </div>
    </div>
  );
}
